msg.payload = {};

if (msg.errors.length) {
    msg.content = msg.errors.join('\n');
}

msg.payload.chatId = msg.id;
msg.payload.type = 'message';
msg.payload.content = msg.content;

return msg;

msg.command = '';
msg.forecast = 0;
msg.errors = []; // array of strings
msg.urlType = '';
msg.id = msg.payload.chatId;
msg.city = 'Denges';
msg.help = false;

let input = msg.payload.content.split(' ');
msg.command = input[0];
if (msg.command === '/help' || msg.command === '/start') {
    msg.help = true;
    return msg;
}

let value = Number(input[1]);

if (input[1]) { // forecast
    if(isNaN(value)) {
        msg.errors.push(`Error: The value entered after command ${input[0]} must be a number.`)
    } else {
        msg.forecast = value;
    }
} else { // weather
    msg.forecast = 0;
}

console.log(msg.errors);


if(msg.forecast || msg.command === '/nextday') {
    msg.urlType = "forecast";
} else {
    msg.urlType = "weather";
}

if(msg.command === '/city') {
    if (input[2] !== undefined) {
        msg.city = input [2];
    } else {
        msg.errors.push('Error: You did not input a city name as the second argument.')
    }
}

msg.appid = env.get("OWM_ID");

msg.payload = {
    "q": msg.city,
    "appid": msg.appid,
    "units": "metric"
}

return msg;

let content = '';

  switch (msg.command) {
    case '/temp':
      content = `Current temperature in Denges is ${msg.payload.main.temp}°C.`;
      break;
    case '/tempfeel':
      content = `Current perceived temperature in Denges is ${msg.payload.main.feels_like}°C.`;
      break;
    case '/humidity':
      content = `Current humidity in Denges is ${msg.payload.main.humidity}%.`;
      break;
    case '/wind':
      content = `Current wind speed in Denges is ${msg.payload.wind.speed}m/s.`;
      break;
    case '/weather':
      content = `Current weather in Denges is ${msg.payload.weather[0].description}.`;
      break;
    case '/clouds':
      content = `Current amount of sky cloudy in Denges is ${msg.payload.clouds.all}%.`;
      break;
    case '/rain':
      content = `Amount of rain fallen during last hour in Denges is ${msg.payload.rain ? msg.payload.rain['1h'] : 0}mm.`;
      break;
    case '/city':
    case '/all':
      content = 
        `Current weather in ${msg.city}\n
        Temperature: ${msg.payload.main.temp}°C
        Perceived temperature: ${msg.payload.main.feels_like}°C
        Humidity: ${msg.payload.main.humidity}%
        Wind speed: ${msg.payload.wind.speed}m/s
        Weather description: ${msg.payload.weather[0].description}
        Cloudiness: ${msg.payload.clouds.all}%
        Rain (last hour): ${msg.payload.rain ? msg.payload.rain['1h'] : 0}mm`;
      break;
    default:
      msg.errors.push('Error: Unknown command for current weather in bot v1!');
      break;
  }

  msg.content = content;

  return msg;

let content = "";

if (msg.forecast > 120) {
    msg.errors.push('Error: You are asking for a forecast in more than 120 hours.');
    return msg;
}
let index = Math.floor(msg.forecast/3);

switch (msg.command) {
    case ("/temp"):
        content = `The temperature in Denges in ${msg.forecast} hours will be ${msg.payload.list[index].main.temp}°C.`
        break;
    case '/tempfeel':
      content = `The perceived temperature in Denges in ${msg.forecast} hours will be ${msg.payload.list[index].main.feels_like}°C.`;
      break;
    case '/humidity':
      content = `The humidity in Denges in ${msg.forecast} hours will be ${msg.payload.list[index].main.humidity}%.`;
      break;
    case '/wind':
      content = `The wind speed in Denges in ${msg.forecast} hours will be ${msg.payload.list[index].wind.speed}m/s.`;
      break;
    case '/weather':
      content = `The weather in Denges in ${msg.forecast} hours will be ${msg.payload.list[index].weather[0].description}.`;
      break;
    case '/clouds':
      content = `The amount of sky cloudy in Denges in ${msg.forecast} hours will be ${msg.payload.list[index].clouds.all}%.`;
      break;
    case '/rain':
      content = `The amount of rain fallen that will fall over three hours in Denges in ${msg.forecast} hours will be ${msg.payload.list[index].rain ? msg.payload.list[index].rain['3h'] : 0}mm.`;
      break;
    case '/city':
    case '/all':
      content = 
        `Predicted weather in ${msg.city} in ${msg.forecast} hours\n
        Temperature: ${msg.payload.list[index].main.temp}°C
        Perceived temperature: ${msg.payload.list[index].main.feels_like}°C
        Humidity: ${msg.payload.list[index].main.humidity}%
        Wind speed: ${msg.payload.list[index].wind.speed}m/s
        Weather description: ${msg.payload.list[index].weather[0].description}
        Cloudiness: ${msg.payload.list[index].clouds.all}%
        Rain (next three hours): ${msg.payload.list[index].rain ? msg.payload.list[index].rain['3h'] : 0}mm`;
      break;
    case '/nextday':
      content = 
        `Predicted weather in ${msg.city} for the next 24h \n (3 hours time slots) \n\n Temperature Perceived temperature   Weather \n`
        for (let i = 0; i<7; i++) {
            content += `${msg.payload.list[i].main.temp.toFixed(2)}°C ${msg.payload.list[i].main.feels_like.toFixed(2)}°C    ${msg.payload.list[i].weather[0].description}\n`
        }
      break;
    default:
      msg.errors.push('Error: Unknown command for forecast in bot v1!');
      break;
}

msg.content = content

return msg;

msg.payload = {};

content = `**List of commands:**\n
/temp - Measured temperature
/tempfeel - Human perceived temperature
/wind - Wind speed
/weather - Weather description -> clear, cloudy, rain, ...
/clouds - Percentage of sky covered with clouds
/rain - Amount of rain fallen
/humidity - Humidity in percents
/all - Get all information given by other commands at once.
/city - Get all information like with "/all" for another city than Denges. City name should be only in lowercase letters.
/nextday - Temperature, perceived temperature and weather description for the next 24h in Denges
/help - Information about all available commands


- All commands excepted /city, /nextday and /help can be followed by a number (we call it time offset), which indicates for when the weather prediction must be (number of hours in the future from now). The maximal value of this number is 120 hours aka 5 days. If no time offset is used, current info is returned.


- /help and /nextday accept no parameters.

- /city must have two parameters. First the time offset, then the name of the city for which you want the forecast, all in lowercase.`;


msg.payload.chatId = msg.id;
msg.payload.type = 'message';
msg.payload.content = content;
msg.payload.options = { 
        parse_mode: "Markdown"
    }

return msg;

